import React, { Component } from "react";
import { Card, CardBody, CardImg, CardTitle, CardText } from "reactstrap";
import Button from "@bit/creative-tim.argon-dashboard-react.button";
import "./CardDefault.css";

class CardDefault extends Component {
  render() {
    return (
      <>
        <Card style={{ width: "18rem", ...this.props.cardStyle }}>
          <CardImg
            alt={this.props.alt || "..."}
            src={this.props.src || "https://picsum.photos/1000/900"}
            top
          />
          <CardBody>
            <CardTitle>{this.props.cardTitle || "Card Title"}</CardTitle>
            <CardText>
              {this.props.cardText ||
                "Some quick example text to build on the card title and make up the bulk of the card's content."}
            </CardText>
            <Button
              color={this.props.buttonColor || "primary"}
              href={this.props.buttonHref || "#pablo"}
              onClick={(e) => this.props.buttonOnClick?.(e)}
            >
              {this.props.buttonText || "Go somewhere"}
            </Button>
          </CardBody>
        </Card>
      </>
    );
  }
}

export default CardDefault;
